// NotaForm.js
import React, { useState } from 'react';
import axios from '../services/api';

const NotaForm = () => {
  const [nota, setNota] = useState('');
  const [progreso, setProgreso] = useState('1'); // Por defecto, se establece en 'Progreso 1'
  const [idBanner, setIdBanner] = useState('');
  const [fecha, setFecha] = useState('');
  const [error, setError] = useState('');

  const handleNotaSubmit = async (e) => {
    e.preventDefault();

    // Validar la nota dentro del rango de 0 a 10
    if (isNaN(nota) || nota < 0 || nota > 10) {
      setError('La nota debe estar entre 0 y 10.');
      return;
    }

    try {
      await axios.post('/nota/agregarNota', { nota, progreso, idBanner, fecha });
      // Lógica adicional después de agregar una nota
      setError(''); // Limpiar el mensaje de error si todo sale bien
    } catch (error) {
      console.error('Error al agregar nota:', error);
      setError('Error al agregar la nota. Por favor, verifica los datos e intenta de nuevo.');
    }
  };

  return (
    <div>
      <h2>Agregar Nota</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleNotaSubmit}>
        <label>
          Nota (entre 0 y 10):
          <input
            type="number"
            value={nota}
            onChange={(e) => setNota(e.target.value)}
            min="0"
            max="10"
          />
        </label>
        <br />
        <label>
          Progreso:
          <select value={progreso} onChange={(e) => setProgreso(e.target.value)}>
            <option value="1">Progreso 1</option>
            <option value="2">Progreso 2</option>
          </select>
        </label>
        <br />
        <label>
          ID Banner:
          <input type="text" value={idBanner} onChange={(e) => setIdBanner(e.target.value)} />
        </label>
        <br />
        <label>
          Fecha:
          <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} />
        </label>
        <br />
        <button type="submit">Agregar Nota</button>
      </form>
    </div>
  );
};

export default NotaForm;
