// NotaForm.js
import React, { useState } from 'react';
import axios from '../services/api';

const NotaForm = () => {
  const [idBannerNota, setIdBannerNota] = useState('');
  const [p1, setP1] = useState(0);
  const [p2, setP2] = useState(0);
  const [nota, setNota] = useState('');
  const [progreso, setProgreso] = useState('');
  const [idBanner, setIdBanner] = useState('');
  const [fecha, setFecha] = useState('');
  const [error, setError] = useState();

  const handleNotaSubmit = async (e) => {
    e.preventDefault();

    if (isNaN(nota) || nota < 0 || nota > 10) {
        setError('La nota debe estar entre 0 y 10.');
        return;
      }

    try {
      await axios.post('/nota/agregarnota', { idBanner: idBannerNota, p1, p2 });
      // Lógica adicional después de agregar una nota
    } catch (error) {
      console.error('Error al agregar nota:', error);
    }
  };

  return (
    <div>
      <h2>Agregar Nota</h2>
      <form onSubmit={handleNotaSubmit}>
        <label>
          Nota:
          <input type="number" value={nota} onChange={(e) => setNota(e.target.value)} />
        </label>
        <br />
        <label>
          Progreso:
          <input type="number" value={progreso} onChange={(e) => setProgreso(e.target.value)} />
        </label>
        <br />
        <label>
          ID Banner:
          <input type="text" value={idBanner} onChange={(e) => setIdBanner(e.target.value)} />
        </label>
        <br />
        <label>
          Fecha:
          <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} />
        </label>
        <br />
        <button type="submit">Agregar Nota</button>
      </form>
    </div>
  );
};

export default NotaForm;
