// AlumnoTable.js
import React, { useState, useEffect } from 'react';
import axios from '../services/api';

const AlumnoTable = () => {
  const [alumnos, setAlumnos] = useState([]);
  const [selectedIdBanner, setSelectedIdBanner] = useState('');
  const [notas, setNotas] = useState([]);
  const [selectedAlumno, setSelectedAlumno] = useState('');

  useEffect(() => {
    const fetchAlumnos = async () => {
      try {
        const response = await axios.get('/alumno/allAlumnos');
        setAlumnos(response.data.alumnos);
      } catch (error) {
        console.error('Error al obtener alumnos:', error);
      }
    };

    fetchAlumnos();
  }, []);

  const handleAlumnoSelect = async (idBanner) => {
    try {
      const response = await axios.get(`/nota/notas/${idBanner}`);
      const notasDelAlumno = response.data; // asumiendo que la respuesta contiene la lista de notas del alumno
      setNotas(notasDelAlumno);
      setSelectedIdBanner(idBanner);
    } catch (error) {
      console.error('Error al obtener notas del alumno:', error);
    }
  };

  const handleDropdownChange = (event) => {
    const idBanner = event.target.value;
    setSelectedAlumno(idBanner);
    handleAlumnoSelect(idBanner);
  };

  return (
    <div>
      <h2>Tabla de Alumnos y Notas</h2>
      <label htmlFor="alumnoDropdown">Selecciona un alumno:</label>
      <select id="alumnoDropdown" value={selectedAlumno} onChange={handleDropdownChange}>
        <option value="">Selecciona un alumno</option>
        {alumnos.map((alumno) => (
          <option key={alumno.idBanner} value={alumno.idBanner}>
            {alumno.nombre} - {alumno.idBanner}
          </option>
        ))}
      </select>

      <table>
        <thead>
          <tr>
            <th>ID Banner</th>
            <th>Nombre</th>
            <th>Notas</th>
          </tr>
        </thead>
        <tbody>
          {alumnos.map((alumno) => (
            <tr key={alumno.idBanner} onClick={() => handleAlumnoSelect(alumno.idBanner)}>
              <td>{alumno.idBanner}</td>
              <td>{alumno.nombre}</td>
              <td>
                <ul>
                  {selectedIdBanner === alumno.idBanner &&
                    notas.map((nota) => (
                      <li key={nota._id}>
                        Progreso {nota.progreso}: {nota.nota}
                      </li>
                    ))}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AlumnoTable;
